ext:createCollection.ext:options:
{
validator: {
   $jsonSchema: {
      bsonType: "object",
      required: [ "name", "year", "major", "address" ],
      properties: {
         name: {
            bsonType: "string",
            description: "must be a string and is required"
         },
         year: {
            bsonType: "int",
            minimum: 2017,
            maximum: 3017,
            description: "must be an integer in [ 2017, 3017 ] and is required"
         },
         major: {
            enum: [ "Math", "English", "Computer Science", "History", null ],
            description: "can only be one of the enum values and is required"
         },
         gpa: {
            bsonType: [ "double" ],
            description: "must be a double if the field exists"
         },
         address: {
            bsonType: "object",
            required: [ "city" ],
            properties: {
               street: {
                  bsonType: "string",
                  description: "must be a string if the field exists"
               },
               city: {
                  bsonType: "string",
                  "description": "must be a string and is required"
               }
            }
         }
      }
   }
},
validationAction: "warn",
validationLevel: "strict"
}

ext:insertOne.ext:document:
{
   name: "Alice",
   year: NumberInt(2019),
   major: "History",
   gpa: 3.0,
   address: {
      city: "NYC",
      street: "33rd Street"
   }
}

ext:runCommand.ext:command:
{ 
   cloneCollectionAsCapped: "inventory",
   toCollection: "capped_inventory",
   size: 100000,
   comment: "This is a capped collection"
 }

ext:insertMany.ext:documents:
[
{ _id: 1, name: "Alto", color: "Red",cno: "H410",speed:40,mfdcountry: "India"},
{ _id: 2, name: "Polo", color: "White",cno: "H411",speed:45,mfdcountry: "Japan" },
{ _id: 3, name: "Audi", color: "Black",cno: "H412",speed:50,mfdcountry: "Germany" }
]

ext:runCommand.ext:command:
{
  findAndModify: "car",
  query: { name: "Alto" },
  sort: { cno: 1 },
  update: { $inc: { speed: 10 } },
  }
